import { motion } from 'framer-motion';

const GoroutinesTitle = () => {
    return (
        <div className="text-center">
            <motion.div
                initial={{ scale: 0.8, opacity: 0 }}
                animate={{ scale: 1, opacity: 1 }}
                transition={{ delay: 0.2, duration: 0.8 }}
            >
                <h1 className="text-8xl font-bold mb-6 bg-gradient-to-r from-purple-500 to-pink-500 bg-clip-text text-transparent">
                    Goroutines: Under the Hood
                </h1>
            </motion.div>

            <motion.p
                initial={{ y: 20, opacity: 0 }}
                animate={{ y: 0, opacity: 1 }}
                transition={{ delay: 0.6 }}
                className="text-2xl text-gray-300 mb-12"
            >
                Scheduler, Stacks, and Context Switching
            </motion.p>
        </div>
    );
};

export default GoroutinesTitle;

import { motion } from 'framer-motion';

const ContextSwitching = () => {
    return (
        <div className="max-w-6xl w-full">
            <h2 className="text-5xl font-bold mb-12 text-center text-purple-400">Context Switching</h2>

            <div className="grid grid-cols-2 gap-16">
                {/* OS Thread Switching */}
                <div className="bg-white/5 p-8 rounded-2xl border border-white/10">
                    <h3 className="text-3xl font-bold mb-6 text-red-400">OS Thread Switch</h3>
                    <div className="space-y-4 text-gray-300 mb-8">
                        <p>Managed by the Kernel.</p>
                        <p>Expensive (~1-2 microseconds).</p>
                        <p>Must save ALL registers (AVX, Floating Point, etc).</p>
                    </div>

                    <div className="relative h-32 bg-black/30 rounded-lg overflow-hidden flex items-center justify-center">
                        <motion.div
                            animate={{ opacity: [0, 1, 0] }}
                            transition={{ duration: 2, repeat: Infinity }}
                            className="text-red-500 font-mono font-bold"
                        >
                            SYSCALL...
                        </motion.div>
                    </div>
                </div>

                {/* Goroutine Switching */}
                <div className="bg-white/5 p-8 rounded-2xl border border-white/10">
                    <h3 className="text-3xl font-bold mb-6 text-green-400">Goroutine Switch</h3>
                    <div className="space-y-4 text-gray-300 mb-8">
                        <p>Managed by the Go Runtime (User Space).</p>
                        <p>Cheap (~200 nanoseconds).</p>
                        <p>Saves only 3 registers (PC, SP, DX).</p>
                    </div>

                    <div className="relative h-32 bg-black/30 rounded-lg overflow-hidden flex items-center justify-center gap-4">
                        <div className="flex gap-2">
                            {['PC', 'SP', 'DX'].map((reg, i) => (
                                <motion.div
                                    key={reg}
                                    initial={{ y: 20, opacity: 0 }}
                                    animate={{ y: 0, opacity: 1 }}
                                    transition={{ delay: i * 0.2, duration: 0.5, repeat: Infinity, repeatDelay: 2 }}
                                    className="px-3 py-1 bg-green-500/20 border border-green-500 rounded text-green-400 text-sm font-mono"
                                >
                                    {reg}
                                </motion.div>
                            ))}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ContextSwitching;

import { motion } from 'framer-motion';

const RuntimeControl = () => {
    return (
        <div className="max-w-6xl w-full px-4">
            <h2 className="text-5xl font-bold mb-12 text-center text-purple-400">Runtime Package</h2>

            <div className="grid grid-cols-1 gap-6">
                {/* GOMAXPROCS */}
                <motion.div
                    initial={{ x: -50, opacity: 0 }}
                    animate={{ x: 0, opacity: 1 }}
                    transition={{ delay: 0.2 }}
                    className="bg-white/5 p-6 rounded-2xl border border-white/10 flex flex-col md:flex-row items-start md:items-center gap-6"
                >
                    <div className="bg-black/30 p-4 rounded-lg font-mono text-purple-300 text-lg md:text-xl flex-shrink-0 w-full md:w-72 text-center">
                        runtime.GOMAXPROCS(n)
                    </div>
                    <div className="flex-1">
                        <h3 className="text-xl md:text-2xl font-bold mb-2 text-white">Parallelism Level</h3>
                        <p className="text-sm md:text-base text-gray-400 leading-relaxed">
                            Sets the number of logical CPUs (P's) that can execute user-level Go code simultaneously.
                            Defaults to <code className="text-purple-300 bg-black/20 px-2 py-1 rounded">runtime.NumCPU()</code>.
                        </p>
                    </div>
                </motion.div>

                {/* Gosched */}
                <motion.div
                    initial={{ x: -50, opacity: 0 }}
                    animate={{ x: 0, opacity: 1 }}
                    transition={{ delay: 0.4 }}
                    className="bg-white/5 p-6 rounded-2xl border border-white/10 flex flex-col md:flex-row items-start md:items-center gap-6"
                >
                    <div className="bg-black/30 p-4 rounded-lg font-mono text-purple-300 text-lg md:text-xl flex-shrink-0 w-full md:w-72 text-center">
                        runtime.Gosched()
                    </div>
                    <div className="flex-1">
                        <h3 className="text-xl md:text-2xl font-bold mb-2 text-white">Yield Processor</h3>
                        <p className="text-sm md:text-base text-gray-400 leading-relaxed">
                            Voluntarily yields the processor, allowing other goroutines to run.
                            The current goroutine is placed back in the local run queue.
                        </p>
                    </div>
                </motion.div>

                {/* Goexit */}
                <motion.div
                    initial={{ x: -50, opacity: 0 }}
                    animate={{ x: 0, opacity: 1 }}
                    transition={{ delay: 0.6 }}
                    className="bg-white/5 p-6 rounded-2xl border border-white/10 flex flex-col md:flex-row items-start md:items-center gap-6"
                >
                    <div className="bg-black/30 p-4 rounded-lg font-mono text-purple-300 text-lg md:text-xl flex-shrink-0 w-full md:w-72 text-center">
                        runtime.Goexit()
                    </div>
                    <div className="flex-1">
                        <h3 className="text-xl md:text-2xl font-bold mb-2 text-white">Terminate Goroutine</h3>
                        <p className="text-sm md:text-base text-gray-400 leading-relaxed">
                            Terminates the calling goroutine. Defer statements are still executed.
                        </p>
                    </div>
                </motion.div>
            </div>
        </div>
    );
};

export default RuntimeControl;

import { motion } from 'framer-motion';

const WorkStealing = () => {
    return (
        <div className="max-w-6xl w-full">
            <h2 className="text-5xl font-bold mb-12 text-center text-purple-400">Work Stealing</h2>

            <div className="flex flex-col items-center gap-12">
                <p className="text-xl text-gray-300 text-center max-w-3xl">
                    When a Processor (P) runs out of work, it attempts to <span className="text-green-400 font-bold">steal</span> half the run queue from another P.
                </p>

                <div className="flex gap-24 items-center">
                    {/* Processor 1 (Idle) */}
                    <div className="flex flex-col items-center gap-4">
                        <div className="w-24 h-24 bg-purple-600/20 border-2 border-purple-500 rounded-lg flex items-center justify-center text-2xl font-bold relative">
                            P1
                            <div className="absolute -top-8 text-sm text-gray-400">Idle</div>
                        </div>
                        {/* Empty Queue */}
                        <div className="w-32 h-40 border-2 border-dashed border-gray-700 rounded-lg flex flex-col-reverse p-2 gap-2 relative">
                            <div className="text-center text-gray-600 text-sm mt-2">Run Queue</div>

                            {/* Stolen Jobs Animation */}
                            {[1, 2].map(i => (
                                <motion.div
                                    key={i}
                                    initial={{ x: 300, opacity: 0 }}
                                    animate={{ x: 0, opacity: 1 }}
                                    transition={{ delay: 2 + (i * 0.2), duration: 1 }}
                                    className="h-8 bg-green-500 rounded flex items-center justify-center text-black font-bold text-xs"
                                >
                                    G{i + 4} (Stolen)
                                </motion.div>
                            ))}
                        </div>
                    </div>

                    {/* Stealing Action */}
                    <motion.div
                        animate={{ x: [0, 50, 0], opacity: [0, 1, 0] }}
                        transition={{ duration: 2, repeat: Infinity }}
                        className="text-4xl text-green-400 font-bold"
                    >
                        ← Steal
                    </motion.div>

                    {/* Processor 2 (Busy) */}
                    <div className="flex flex-col items-center gap-4">
                        <div className="w-24 h-24 bg-purple-600 border-2 border-purple-500 rounded-lg flex items-center justify-center text-2xl font-bold relative">
                            P2
                            <div className="absolute -top-8 text-sm text-green-400">Busy</div>
                        </div>
                        {/* Full Queue */}
                        <div className="w-32 h-40 border-2 border-gray-700 rounded-lg flex flex-col-reverse p-2 gap-2 overflow-hidden">
                            <div className="text-center text-gray-600 text-sm mt-2">Run Queue</div>
                            {[1, 2, 3, 4].map(i => (
                                <motion.div
                                    key={i}
                                    initial={{ opacity: 1 }}
                                    animate={i > 2 ? { x: -300, opacity: 0 } : {}}
                                    transition={{ delay: 2 + ((i - 2) * 0.2), duration: 1 }}
                                    className="h-8 bg-green-500 rounded flex items-center justify-center text-black font-bold text-xs"
                                >
                                    G{i}
                                </motion.div>
                            ))}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default WorkStealing;

import { motion } from 'framer-motion';

const Preemption = () => {
    return (
        <div className="max-w-6xl w-full">
            <h2 className="text-5xl font-bold mb-12 text-center text-purple-400">Asynchronous Preemption</h2>

            <div className="grid grid-cols-2 gap-16 items-center">
                <div className="space-y-6">
                    <div className="bg-white/5 p-6 rounded-xl border border-white/10">
                        <h3 className="text-xl font-bold text-gray-400 mb-2">Before Go 1.14 (Cooperative)</h3>
                        <p className="text-gray-300">
                            Preemption only happened at function calls. Tight loops like <code className="text-red-400">for {'{}'}</code> could hang the scheduler/GC.
                        </p>
                    </div>

                    <div className="bg-green-500/10 p-6 rounded-xl border border-green-500/30">
                        <h3 className="text-xl font-bold text-green-400 mb-2">Go 1.14+ (Preemptive)</h3>
                        <p className="text-gray-300">
                            The runtime sends a signal (<code className="text-purple-300">SIGURG</code>) to threads running for too long (&gt;10ms), forcing them to yield.
                        </p>
                    </div>
                </div>

                <div className="relative h-64 bg-black/30 rounded-xl border border-gray-700 flex items-center px-8 overflow-hidden">
                    {/* Timeline */}
                    <div className="absolute bottom-8 left-0 w-full h-px bg-gray-600" />

                    {/* Long Running Goroutine */}
                    <motion.div
                        initial={{ width: 0 }}
                        animate={{ width: "100%" }}
                        transition={{ duration: 4, ease: "linear" }}
                        className="h-12 bg-blue-500 rounded relative"
                    >
                        <span className="absolute left-4 top-3 text-black font-bold text-sm">Long Loop...</span>
                    </motion.div>

                    {/* Signal Interrupt */}
                    <motion.div
                        initial={{ opacity: 0, scale: 0 }}
                        animate={{ opacity: 1, scale: 1 }}
                        transition={{ delay: 2, duration: 0.2 }}
                        className="absolute left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2 z-10"
                    >
                        <div className="bg-red-500 text-white px-4 py-2 rounded-full font-bold shadow-[0_0_20px_rgba(239,68,68,0.8)] animate-pulse">
                            SIGURG
                        </div>
                    </motion.div>

                    {/* Yield Effect */}
                    <motion.div
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        transition={{ delay: 2.2 }}
                        className="absolute right-4 top-4 text-green-400 font-mono"
                    >
                        -&gt; Yielded
                    </motion.div>
                </div>
            </div>
        </div>
    );
};

export default Preemption;

import { motion } from 'framer-motion';

const GoroutineStates = () => {
    const states = [
        { id: 'idle', label: '_Gidle', color: 'gray' },
        { id: 'runnable', label: '_Grunnable', color: 'blue' },
        { id: 'running', label: '_Grunning', color: 'green' },
        { id: 'waiting', label: '_Gwaiting', color: 'yellow' },
        { id: 'dead', label: '_Gdead', color: 'red' },
    ];

    return (
        <div className="max-w-6xl w-full">
            <h2 className="text-5xl font-bold mb-16 text-center text-purple-400">Goroutine Lifecycle</h2>

            <div className="flex justify-center items-center gap-8 relative h-96">

                {/* State Nodes */}
                {states.map((state, index) => (
                    <div key={state.id} className="relative z-10 flex flex-col items-center">
                        <motion.div
                            initial={{ scale: 0 }}
                            animate={{ scale: 1 }}
                            transition={{ delay: index * 0.2 }}
                            className={`w-32 h-32 rounded-full border-4 border-${state.color}-500 bg-${state.color}-500/10 flex items-center justify-center text-xl font-bold text-${state.color}-400 shadow-[0_0_20px_rgba(0,0,0,0.5)]`}
                        >
                            {state.label}
                        </motion.div>
                    </div>
                ))}

                {/* Arrows (Simplified for visual flow) */}
                <svg className="absolute inset-0 w-full h-full pointer-events-none z-0">
                    <defs>
                        <marker id="arrowhead" markerWidth="10" markerHeight="7" refX="9" refY="3.5" orient="auto">
                            <polygon points="0 0, 10 3.5, 0 7" fill="#4B5563" />
                        </marker>
                    </defs>
                    {/* Idle -> Runnable */}
                    <line x1="18%" y1="50%" x2="28%" y2="50%" stroke="#4B5563" strokeWidth="2" markerEnd="url(#arrowhead)" />
                    {/* Runnable -> Running */}
                    <line x1="38%" y1="50%" x2="48%" y2="50%" stroke="#4B5563" strokeWidth="2" markerEnd="url(#arrowhead)" />
                    {/* Running -> Waiting */}
                    <path d="M 55% 40% Q 60% 10% 65% 40%" stroke="#4B5563" strokeWidth="2" fill="none" markerEnd="url(#arrowhead)" />
                    {/* Waiting -> Runnable */}
                    <path d="M 65% 60% Q 60% 90% 55% 60%" stroke="#4B5563" strokeWidth="2" fill="none" markerEnd="url(#arrowhead)" />
                    {/* Running -> Dead */}
                    <line x1="58%" y1="50%" x2="88%" y2="50%" stroke="#4B5563" strokeWidth="2" markerEnd="url(#arrowhead)" strokeDasharray="5,5" />
                </svg>

                {/* Labels for transitions */}
                <div className="absolute top-1/4 left-[60%] text-xs text-gray-500">Channel/Syscall</div>
                <div className="absolute bottom-1/4 left-[60%] text-xs text-gray-500">Ready</div>
            </div>
        </div>
    );
};

export default GoroutineStates;

import { motion } from 'framer-motion';

const Summary = () => {
    return (
        <div className="max-w-4xl w-full text-center">
            <h2 className="text-6xl font-bold mb-16 bg-gradient-to-r from-go-blue to-purple-500 bg-clip-text text-transparent">
                Key Takeaways
            </h2>

            <div className="grid grid-cols-1 gap-6 text-left">
                {[
                    "Concurrency is not Parallelism",
                    "Goroutines are cheap and lightweight",
                    "Channels orchestrate communication",
                    "Don't communicate by sharing memory",
                    "Use WaitGroups to sync execution"
                ].map((item, index) => (
                    <motion.div
                        key={index}
                        initial={{ opacity: 0, x: -50 }}
                        animate={{ opacity: 1, x: 0 }}
                        transition={{ delay: index * 0.1 + 0.5 }}
                        className="flex items-center gap-6 bg-white/5 p-6 rounded-xl border border-white/10 hover:bg-white/10 transition-colors"
                    >
                        <div className="w-8 h-8 rounded-full bg-go-blue flex items-center justify-center font-bold text-black">
                            {index + 1}
                        </div>
                        <span className="text-2xl text-gray-200">{item}</span>
                    </motion.div>
                ))}
            </div>

            <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delay: 2 }}
                className="mt-16 text-gray-400"
            >
                Thanks for watching!
            </motion.div>
        </div>
    );
};

export default Summary;
